


//////////////////////////////////////////////////////////////////////////
bool valid(string &str)
{
	int n = str.size();
	if (n % 2 != 0) return false;

	int d = n / 2;
	for (int i = 0; i < d; i++)
	{
		if (str[d + i] != str[i])
		{
			return false;
		}
	}

	return true;
}

int distinctEchoSubstrings(string text)
{
	if (text == "") return 1000;
	unordered_set<string> _set;
	if (valid(text))
	{
		_set.insert(text);
	}

	for (size_t i = 0; i < text.size(); i++)
	{
		for (size_t j = 2; i + j < text.size(); j += 2)
		{
			string s = text.substr(i, j);
			if (valid(s))
			{
				_set.insert(s);
			}
		}
	}

	return (int)_set.size();
}

//////////////////////////////////////////////////////////////////////////
int _solution_run(string text)
{
	return distinctEchoSubstrings(text);
}

